package aula15_VetorMatriz;

public class VetorExemplo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] vetor = new int[10]; //começa na pos 0.....9
		
		for (int i = 0; i <= 9; i++) {
			vetor[i] += 1;
			System.out.println(i + " - " + vetor[i]);
			
			
		}
		
		
	}

}
